import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import { pokemonDetail } from "../../redux/actions";
import style from './Detail.module.css';

const Detail = (props) => {
    let par = useParams();
    console.log(par.id)
    console.log(props);
    const dispatch = useDispatch();
    const poke = useSelector((state) => state.detail);
    console.log(poke);
    useEffect(() => {
        dispatch(pokemonDetail(par.id));
    }, [dispatch, props.match.params.id]);


    return(
        <div>
            {
                poke.length > 0 ? (
                    <div>
                        <h1>{poke[0].name}</h1>
                        <img src={poke[0].image} alt={`Pokemon ${poke[0].name}`} />
                        <h3>{poke[0].id}</h3>
                        <p>{poke[0].hp}</p>
                        <p>{poke[0].attack}</p>
                        <p>{poke[0].defense}</p>
                        <p>{poke[0].speed}</p>
                        <p>{poke[0].height}</p>
                        <p>{poke[0].weight}</p>
                        {poke.types?.map((element) => (<span key={element.idSearch} className={element.name}>{element.name}</span>))}
                    </div>                    
                )
                : <p>Loading</p>
            }
            <Link to='/home'><button>Back</button></Link>
        </div>
    )
}

export default Detail;
