import style from './Home.module.css';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPokemon } from '../../redux/actions';
import Card from '../../components/Card/Card';

const Home = () => {

    const dispatch = useDispatch();
    const allPokemons = useSelector((state) => state.allPokemons);
    const [currentPage, setCurrentPage] = useState(1);
    const [charPerPage, setCharPerPage] = useState(12);
    const indexLastChar = currentPage * charPerPage;
    const indexFirstChar = indexLastChar - charPerPage;
    const currentChars = allPokemons.slice(indexFirstChar, indexLastChar)

    useEffect(()=>{
        dispatch(getPokemon())
    },[dispatch]);


    return(
        <div className={style.container}>
            <h1>Home</h1>
                <div className={style.cardContainer}>
                    {allPokemons?.map((el) => {
                        return (
                            <div key={el.id}>
                                <Card key={el.id} image={el.image} name={el.name} id={el.id} type={el.types} />
                            </div>
                        )
                    })}
                </div>

        </div>
    )
}

export default Home;
