const validateUrl = (URL) => {
    const regex = new RegExp(/(https?:\/\/.*\.(?:png|jpg|svg))/);
    return regex.test(URL);
};


export const validate = (info) => {
    let error = {};

    if(info.name.length > 0 && info.name.length < 4) error.name = 'The name must be longer than 4 lettersðŸ˜ðŸ˜!!';
    if(info.name.length > 15) error.name = 'The name cannot be longer than 15 letters';
    if(info.defense <= 0) error.defense = 'Defense is needed';
    if(typeof info.defense) error.defense = 'Defense must be a number';
    if(info.attack <= 0) error.attack = 'Attack is needed';
    if(typeof info.attack) error.defense = 'attack must be a number';
    if(info.height <= 0) error.height = 'Height is needed';
    if(typeof info.height) error.defense = 'Height must be a number';
    if(info.weight <= 0) error.weight = 'Weight is needed';
    if(typeof info.weight) error.defense = 'Weight must be a number';
    if(info.speed <= 0) error.speed = 'Speed is needed';
    if(typeof info.speed) error.defense = 'Speed must be a number';
    if(!info.name) error.name = 'The name is needed';
    if(info.hp <= 0) error.hp = 'Need hpðŸ¤”ðŸ¤”!!';
    if(validateUrl(info.image) && info.image !== '') error.image = 'Unsupported formatðŸ¤”ðŸ¤”!!';
    return error;
}
