const initialState = {
    allPokemons: [],
    types: []
}

function rootReducer (state = initialState, action) {
    switch(action.type) {
        case 'GET_POKEMONS':
            return{
                ...state,
                allPokemons: action.payload
            }

        case 'GETALLTYPES':
            return {
                ...state,
                types: action.payload
            };

        case 'TYPE_FILTERED':
            const poke = state.allPokemons;
            const filter = action.payload === 'all' ? poke : poke.filter((el) => el.types.includes(action.payload));
            console.log(poke)
            return {
                ...state,
                allPokemons: filter
            }

        default :
            return state
    }
}

export default rootReducer;
