const initialState = {
    allPokemons: [],
    types: []
}

function rootReducer (state = initialState, action) {
    switch(action.type) {
        case 'GET_POKEMONS':
            return{
                ...state,
                allPokemons: action.payload
            }

            case 'GETALLTYPES':
                return {
                    ...state,
                    types: action.payload
                };

        default :
            return state
    }
}

export default rootReducer;
