import { Link, useHistory } from 'react-router-dom';
import { getAllTypes, postPokemon } from '../../redux/actions';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect, useState } from 'react';

const Form = () => {
    const dispatch = useDispatch();
    const types = useSelector((state) => state.types);

    const [input, setInput] = useState({
        name: '', hp: '', speed: '', height: '', attack: '', weight: '', defense: '', types: [], image: ''
    });

    useEffect(() => {
        dispatch(getAllTypes());
    }, []);

    const handleChange = (e) => {
        setInput={
            ...input,
            [e.target.name] : e.target.value
        }
    }

    const handlerCheckBox = () => {}

    return(
        <div>
            <Link to='/home'><button>Back</button></Link>
            <h1>Crea tu Pokemon</h1>
            <form>
                <div>
                    <label>Name:</label>
                    <input type="text" value={input.name} name='name' />
                </div>
                <div>
                    <label>Health Points:</label>
                    <input type="text" value={input.hp} name='hp' />
                </div>
                <div>
                    <label>Speed: </label>
                    <input type="text" value={input.speed} name='speed' />
                </div>
                <div>
                    <label>Height: </label>
                    <input type="text" value={input.height} name='height' />
                </div>
                <div>
                    <label>Attack: </label>
                    <input type="text" value={input.attack} name='attack' />
                </div>
                <div>
                    <label>Weight: </label>
                    <input type="text" value={input.weight} name='weight' />
                </div>
                <div>
                    <label>Defense: </label>
                    <input type="text" value={input.defense} name='defense' />
                </div>
                <div className='types'>
                    {types?.map((element) => {
                        return (
                            <div >
                                <label htmlFor="types" >{element}</label>
                                <input type="checkbox" name={element} value={element} onChange={handlerCheckBox} />
                            </div>
                        )
                    })}
                </div>
                <div>
                    <label>Image: </label>
                    <input type="text" value={input.image} name='image' />
                </div>

                <button type='submit'>Create Pokemon</button>
            </form> 
        </div>
    )
}

export default Form;
